create table ORDERS
(
    ID          VARCHAR2(32) default SYS_GUID() not null
        primary key,
    ORDERNUM    VARCHAR2(20)                    not null
        unique,
    ORDERTIME   TIMESTAMP(6),
    PEOPLECOUNT NUMBER,
    ORDERDESC   VARCHAR2(500),
    PAYTYPE     NUMBER,
    ORDERSTATUS NUMBER,
    PRODUCTID   VARCHAR2(32)
        references PRODUCT,
    MEMBERID    VARCHAR2(32)
        references MEMBER
)
/

